import { Page, LaunchOptions, PDFOptions, CommandOptions } from '@condorhero/vuepress-plugin-export-pdf-core';
import { Theme, Bundler } from 'vuepress';
export { registerCommands } from './press-export-pdf.js';

type UserSorter = (a: Page, b: Page) => number;
/**
 * defined user config
 */
interface UserConfig {
    theme?: Theme;
    bundler?: Bundler;
    sorter?: UserSorter;
    puppeteerLaunchOptions?: LaunchOptions;
    routePatterns?: string[];
    pdfOptions?: PDFOptions;
    outFile?: string;
    outDir?: string;
    pdfOutlines?: boolean;
    urlOrigin?: string;
    outlineContainerSelector?: string;
}

declare const moduleRequire: NodeRequire;
declare const serverApp: (dir?: string, commandOptions?: CommandOptions) => Promise<never>;

/**
 * defineUserConfig is a function that defines the user config.
 * @param config - UserConfig
 * @returns config - UserConfig
 */
declare const defineUserConfig: (config: UserConfig) => UserConfig;

export { UserConfig, UserSorter, defineUserConfig, moduleRequire, serverApp };
